import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

export default function Login() {
  const [credentials, setCredentials] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch("http://localhost:5000/api/loginuser", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ email: credentials.email, password: credentials.password })
    });
    const json = await response.json();
    console.log(json);
    if (!json.success) {
      alert("Enter valid credentials");
    }
    if (json.success) {
      localStorage.setItem("userEmail", credentials.email);
      localStorage.setItem("authtoken", json.token); // Assuming token is returned
      navigate("/dashboard"); // Redirect to dashboard or any other route with navbar
    }
  };

  const onChange = (event) => {
    setCredentials({ ...credentials, [event.target.name]: event.target.value });
  };

  return (
    <div  style={{backgroundImage: 'url("https://images.pexels.com/photos/326278/pexels-photo-326278.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1")', height: '100vh', backgroundSize: 'cover' }}>
      <div>
        <Navbar />
      </div>
      <div className='container'>
        
        <form className='w-50 m-auto mt-5 border bg-dark border-success rounded' onSubmit={handleSubmit}>
  <div className="text-center"> {/* Added text-center class to center align text */}
    <h2 className="text-white mb-3">LOGIN</h2> {/* Removed justify-content-center and align-items-center classes */}
    <div className="m-3">
      <label htmlFor="exampleInputEmail1" className="form-label text-white fs-5 fw-bold">Email address</label>
      <input type="email" className="form-control" name='email' value={credentials.email} onChange={onChange} aria-describedby="emailHelp" />
      <div id="emailHelp" className="form-text text-white">We'll never share your email with anyone.</div>
    </div>
    <div className="m-3">
      <label htmlFor="exampleInputPassword1" className="form-label text-white fs-5 fw-bold">Password</label>
      <input type="password" className="form-control" value={credentials.password} onChange={onChange} name='password' />
    </div>
    <button type="submit" className="m-3 btn btn-success fw-bold">Submit</button>
    <Link to="/signup" className="m-3 mx-1 btn btn-danger fw-bold">New User</Link>
  </div>
</form>

      </div>
    </div>
  )
}
